handlers.handler:
// preserve original behaviour if meta key is held
if (event.metaKey) return;

// execute proper scroll
if (event.deltaY < 0) {
    gBrowser.tabContainer.advanceSelectedTab(-1, true);
}
else {
    gBrowser.tabContainer.advanceSelectedTab(1, true);
}

// capture event
event.stopPropagation();
event.preventDefault();
